);        $("#comment_box_id_"+commentId).show();    }    function postHideReply (commentId) {        $("#comment_box_id_"+commentId).hide();    }    function commentReply(replyId) {	    //alert('Reply ID '+ replyId);	    $('#reply_box_id_'+replyId).show();    }    function commetHideReply(replyId) {	   $('#reply_box_id_'+replyId).hide();    }</script>